def eh_vogal(letra):
    vogais = "aeiouAEIOU"
    if letra in vogais:
        return True
    return False

texto = input()

saida = ""
for x in texto:
    if x == " ":
        saida += " "
    elif eh_vogal(x):
        saida += "v"
    else:
        saida += "c"

print (saida)

#include <stdio.h>

char identifica (char c){
	char original = c;
	if(c >= 'A' && c <= 'Z')
		c = c + ('a' - 'A');        // preve caso a letra seja maiuscula

	if(c == 'a' || c == 'e' || c == 'i' || c == 'o' || c == 'u') return 'v';
	if(c >= 'a' && c <= 'z') return 'c';
	return original;
}

int main () {
	int i;
	char frase[100];
	scanf("%100[^\n]", frase);

	for(i = 0; frase[i]; i++){
		printf("%c", identifica(frase[i]) );
	}	
    printf("\n");
	return 0;
}
